mapper.select:
select p.id,p.pmoney,p.pincome,p.ptime,b.bname as "biao.bname",b.id as "biao.id" , p.pway,p.pcount,p.progress,p.psaveway,p.prateben,p.ppublishtime,p.pname,p.ptotalmoney,p.prange,p.puse,p.pstate,p.picture,p.pproduce ,p.pdesc,p.psafe, u.uname as "users.uname" from product p LEFT JOIN users u ON p.pproduce=u.uid LEFT JOIN biao b on b.id=p.ptype where 1=1
and b.id = #{params.ptype}
limit #{params.startPage},#{params.pageSize}

select * from product p where 1 = 1

	and p.ptype = #{biaoId}


	and p.pincome between #{startR} and #{endR}


	and p.pincome between #{startR} and (select max(pincome) from
	product)


	and (select to_days(pcount) - to_days(ptime) from product pt where
	pt.id = p.id) between #{startT} and #{endT}


	and (select to_days(pr.pcount) - to_days(pr.ptime) from
	product pr where
	pr.id = p.id) between #{startT} and (select
	max(pro.cday) from
	(select (to_days(pcount) - to_days(ptime)) as cday
	from product) as
	pro)


	and p.pway = #{pway}


	limit #{startPage},#{pageSize}

mapper.update:
update product
set
progress = #{progress}
where id = #{id}

update product
set
pincome = #{pincome},ptime = #{ptime},pcount = #{pcount}
where id =
#{id}
